import { Component } from '@angular/core';
import {Meal} from '../meals/meal';
import {data} from '../../assets/data/data';
import { OnInit } from '@angular/core';
@Component({
  selector: 'app-meals',
  templateUrl: './meals.component.html',
  styleUrls: ['./meals.component.css']
})
export class MealsComponent implements OnInit{
  list!: Meal[];

  alterState: string = "none";

  name!:string; image!:string;
  category!: string; price!: number;
  created!: Date;
  Ingredients!: Array<string>;

  constructor(){}

  ngOnInit(){
    this.list=data;
  }

  receiveDetails(meal:Meal){
    this.name =meal.name;
    this.image = meal.image;
    this.category = meal.category;
    this.price = meal.price;
    this.created = meal.created;
    this.Ingredients = meal.Ingredients;
    this.image = meal.image;
    this.alterState= "block";
  }

  toggleAlert(): void{
    if(this.alterState=="block"){
      this.alterState="none";
    }else{
      this.alterState="block";
    }
  }

}

