import { Component, Input } from '@angular/core';
import { Meal } from '../meals/meal';

@Component({
  selector: 'app-meal',
  templateUrl: './meal.component.html',
  styleUrls: ['./meal.component.css']
})
export class MealComponent {

  @Input() Content! : Meal;
  constructor(){}

  ngOnInit(){}

}
<div [ngClass="'item'"]>
    <div [ngClass]="'item-content'">
        <img [src]="Content.image" alt="no image" [ngStyle]="{'width':'100%','height':'auto'}">
    </div>
    <div [ngClass]="'item-content'">
        <h3 [ngClass]="'item-title'">{{Content.name|uppercase}}</h3>
        <p>Category : </p>
    </div>
</div>
