import { Component } from '@angular/core';
import {Meal} from '../meals/meal';
import {data} from '../../assets/data/data';
import { OnInit } from '@angular/core';
@Component({
  selector: 'app-meals',
  templateUrl: './meals.component.html',
  styleUrls: ['./meals.component.css']
})
export class MealsComponent implements OnInit{
  list!: Meal[];

  constructor(){}

  ngOnInit(){
    this.list=data;
  }

}

<div class="listview">
    <!--<div [ngClass]="'item'" *ngFor="let item of list">
        <div ngClass="'item-content'">!-->
            <img src="{{item.image}}" alt="no image" [ngStyle]="{'width':'100%', 'height':'auto'}">
        </div>
        <div class="item-content"> 
            <h3 [ngClass]="'item-title'">{{item.name | uppercase}}</h3>
            <p>Category : {{item.category}}, <span [ngClass]="'item-price'">{{item.price}}</span></p>
            <p>Created at : {{item.created | mydate}}</p>
        </div>
    <!--</div>!-->
    <div [ngClass]="'listview'">
        <app-meal [Content]="list[0]"></app-meal>
        <app-meal [Content]="list[1]"></app-meal>
        <app-meal [Content]="list[2]"></app-meal>
    </div>
